        .then(userAuth => {
          dispatch(login({
            email: userAuth.user.email,
            uid: userAuth.user.uid,
            username: userAuth.user.username,
            firstName : userAuth.user.firstName,
            lastName : userAuth.user.lastName,
            position :userAuth.user.position,
            company : userAuth.user.company,        
            role : userAuth.user.role,
            address: userAuth.user.address,
            phoneNumber :userAuth.user.phoneNumber,
          }))
        })
        .catch(error => alert(error));
    };
   return (
     <div className="background">

<div style={{backgroundColor:'white'}} className="login"> 
            <img src={logo} alt=""/>
            
            <form>
                <input value={name} onChange={e => setName(e.target.value)} placeholder="Usename" type="text"/>
                <input value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" type="password"/>
                <button type="submit" onClick={loginToApp}>Sign In</button>
            </form>
           <p>New to Linkedin? <a href='/signup' className="aa"> Join now</a></p>
        </div>
     </div>
      
    )
}
export default Login
