<TableContainer >  
 <header style={{borderBottom:'1px solid rgba(0,0,0,0.08)',position:'sticky', zIndex:'1'}}>
   <div style={{ padding:' 8px 12px 8px 10px',borderLeft:'solid 2px #eb0392' , alignItems:'center' , display:'flex' ,height:'56px'}}>
    <h1 style={{fontSize:'18px',fontWeight:'400',lineHeight:'1.5'}}>Jobs based on your Profile</h1>
   </div>
 </header>
   {filteredList.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map(({ id,data :{  titre, company, address ,createdAt ,users,link } }) => (
     <TabNavItem key={id}> 
      <FlipMove>
         <OffreCardsLeft
       key={id}
       title={titre}
       company={company}
       addresse ={address}
       date ={createdAt}
       users={users}
       link ={link}
     
       />
         </FlipMove>
       </TabNavItem>
   ))}
   <div style={{marginLeft:'30px' ,width:'410px', backgroundColor:'red'}}>
   <TablePagination

      rowsPerPageOptions={[5, 10, 15]}
      
      count={filteredList.length}
      rowsPerPage={rowsPerPage}
      page={page}
      onChangePage={handleChangePage}
      onChangeRowsPerPage={handleChangeRowsPerPage}
    />
   </div>
   
    </TableContainer> 

</TabNav>
<TabContent >
<div></div>
   {filteredList.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map(({ id,data :{  titre, industry,company, address ,createdAt,description ,longitude,latitude, salary , poste , jobTime , type,creator,users,saves,link }  }) => (
     <TabPanel key={id}>
       <FlipMove>
         <OffreCardRight
       key={id}
       id={id }
       title={titre}
       company={company}
       addresse ={address}
       date ={createdAt}
       description={description} 
       salary={salary}
       longitude={longitude}
       latitude={latitude}
       industry= {industry}
       poste={poste}
       jobTime={jobTime}
       type ={type}
       creator={creator}
       users={users}
       saves={saves}
       link={link}
       selected={selected}
       ref={id}
       />
       </FlipMove>
    
       </TabPanel>
   ))}

  
<div ref={div} style={{margin:'10px',padding:'15px',borderRadius:'15px',backgroundColor:'white',border:'1px solid rgba(0, 0, 0, 0.08)'}}>
       <h4 style={{marginBottom:'30px',fontSize:'19px',fontWeight:'500'}}>See your commute</h4>
       <div 
        ref={mapWrapper} 
        className="mapWrapper" 
      >
          </div>
          </div>

  
</TabContent>
</Tabs>

}


    return (
      <div className="offres">
       <Header/>
       <div className="searchBar">
         <div className="items">
          
    {filterList.map(filterr=>(
            
           <button className={ activeFilter[filterr.value]? "filter_button_selected": "filter_button"} onClick={(event)=>handleClick(filterr,event)}>
           {filterr.value}
           <ArrowDropDownIcon/>
           </button>
    ))}    
                  <Menu
                  id="simple-menu"
                  anchorEl={anchorEl}
                  open={Boolean(anchorEl)}
                  onClose={handleClose}
                  >  
                    <form>
                  {
                  filter!=="company" ?
                   clicked.map(ha=>(
           <MenuItem>
         
             <label htmlFor={ha} >{ha}</label>
             <input
             id={ha}
             type="checkbox"
            
             defaultChecked={checked.includes(ha)}
             onClick={() => onFilterChange(ha,filter)}
             
             />
         
           </MenuItem>
           
           

))
:

companies.map(ha=>(
  <MenuItem>

    <label htmlFor={ha} >{ha}</label>
    <input
    id={ha}
    type="checkbox"   
    defaultChecked={checked.includes(ha)}
    onClick={() => onFilterChange(ha,filter)}   
    />

  </MenuItem>
))}
    </form>
          </Menu>
       </div>
        
       </div>     
         {tabs}
     </div>
             
    )
  }
export default Offres ;
