{
  if(window.confirm('Are you sure?'))
  {
   
  axios.delete(`${url}test/delete/`+id.id)
  window.location.reload()

   
  }
}
      
return (


    <div className="app">
       <Header />
          
        <div className="app__body">
       
        <div  >
        <div> <table>
  <tr>
  <th> question</th>
  <th>reponseA</th>
  <th>reponseB</th>
  
  <th>reponseC</th>
  <th>reponseD</th>
  <th>answer</th>
  <th>sujet</th>
  <th></th>
  </tr> 
                    {tests.map(
                    ({id, question, reponseA, reponseB, reponseC,reponseD,answer,sujet }) => {
                      return (

                        <tr>
                        <td>{question}</td>
                        <td>{reponseA}</td>
                        <td>{reponseB}</td>
                        <td>{reponseC}</td>
                        <td>{reponseD}</td>
                        <td>{answer}</td>
                        <td>{sujet}</td>

                       <td><button class="btn" onClick={()=>deleteTest({id})}><FaTrash/></button></td> 
                      
                        </tr>
 
                        
                      );

                    })}</table> </div>
                            </div>
        
      </div>
        
    </div>
);


}

export default Acceuil
