import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { login, selectUser } from './features/userSlice';
import {auth} from './firebase';
import logo from './assets/Linkedin-logo.png'
import './Login.css'
import { useHistory } from 'react-router';
import firebase from 'firebase'

function Login() {
  let history = useHistory();
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [user,setUser] = useState();
    const dispatch = useDispatch();

    const loginToApp = e => {
      e.preventDefault();
      auth.signInWithEmailAndPassword(email, password)
        .then(
      userRecord => {
      firebase.firestore().collection('users').doc('dOJftmcz9CPnQBQMCH6DE1qnNDC3').get().onSnapshot(
        snapshot => (

          setUser(snapshot.docs.map(doc => (
            {
              uid: doc.id,
              data: doc.data(),
            }
            )
            )
            )
             )

   , 
     dispatch(login({
  
      username : user.data.username,
      firstName : user.data.firstN,
      lastName : user.data.lastN,
      position : user.data.position ,
      company : user.data.company,         
      role : user.data.type,
      address:user.data.address,
      phoneNumber : user.data.phoneNumber,  
  }))
         
        
        )
        
          history.push('/')
      })
        .catch(error => alert(error));
    };
   return (
     <div className="background">

<div style={{backgroundColor:'white'}} className="login"> 
            <img src={logo} alt=""/>
            
            <form>
                <input value={email} onChange={e => setEmail(e.target.value)} placeholder="Usename" type="text"/>
                <input value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" type="password"/>
                <button type="submit" onClick={loginToApp}>Sign In</button>
            </form>
           <p>New to Linkedin? <a href='/signup' className="aa"> Join now</a></p>
        </div>
     </div>
      
    )
}
export default Login
